import {
    MagnifyingGlassIcon,
    ChevronUpDownIcon,
  } from "@heroicons/react/24/outline";
  import {
    EyeIcon,
    TrashIcon
  } from "@heroicons/react/24/solid";
  import {
    Card,
    CardHeader,
    Input,
    Typography,
    CardBody,
    Chip,
    CardFooter,
    IconButton,
    Tooltip,
    Dialog
  } from "@material-tailwind/react";
  import { DefaultPagination } from "./pagination"; 
  import { useState, useEffect } from "react";
   
  export function SortableTable({thead, trows, name, actions, ram, kill}) {
    const [currentPage, setCurrentPage] = useState(1)
    const [currentItems, setCurrentItems] = useState([]);
    const [valorBuscado, setValorBuscado] = useState('');
    const indexOfLastItem = currentPage * 5;
    const indexOfFirstItem = indexOfLastItem - 5;
    const [open, setOpen] = useState(false);
    const handleOpen = () => setOpen((cur) => !cur);
    
    useEffect(() => {
      // Filtrar solo si valorBuscado no está vacío
      if (valorBuscado.trim() === '') {
        setCurrentItems(trows.slice(indexOfFirstItem, indexOfLastItem));
      } else {
        const resultadosFiltrados = trows
          .slice(indexOfFirstItem, indexOfLastItem)
          .filter((objeto) => {
            return (
              objeto.PID.toString().includes(valorBuscado.toLowerCase()) ||
              objeto.Name.toLowerCase().includes(valorBuscado.toLowerCase())
            );
          });
        setCurrentItems(resultadosFiltrados);
      }
    }, [valorBuscado]);

    const onChangeInput = ({ target })=>{
      setValorBuscado(target.value);
    }

    return (
      <Card className="h-full w-full">
        <CardHeader floated={false} shadow={false} className="rounded-none">
          <div className="mb-8 flex items-center justify-between gap-8">
            <div>
              <Typography variant="h5" color="blue-gray">
                Lista de {name}
              </Typography>
              <Typography color="gray" className="mt-1 font-normal">
                Ver informacion de todos los {name}
              </Typography>
            </div>
          </div>
          <div className="flex flex-col items-center justify-end gap-4 md:flex-row">
            <div className="w-full md:w-72">
              <Input
                label="Buscar"
                icon={<MagnifyingGlassIcon className="h-5 w-5" />}
                value={valorBuscado}
                onChange={onChangeInput}
              />
            </div>
          </div>
        </CardHeader>
        <CardBody className="overflow-scroll px-0">
          <table className="mt-4 w-full min-w-max table-auto text-left">
            <thead>
              <tr>
                {thead.map((head, index) => (
                  <th
                    key={head}
                    className="cursor-pointer border-y border-blue-gray-100 bg-blue-gray-50/50 p-4 transition-colors hover:bg-blue-gray-50"
                  >
                    <Typography
                      variant="small"
                      color="blue-gray"
                      className="flex items-center justify-between gap-2 font-normal leading-none opacity-70"
                    >
                      {head}{" "}
                      {index !== thead.length - 1 && (
                        <ChevronUpDownIcon strokeWidth={2} className="h-4 w-4" />
                      )}
                    </Typography>
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              { currentItems?.map(
                ({Id_proceso, PID, Name, User, State, Memory, Child}, index) => {
                  const isLast = index === thead.length - 1;
                  const classes = isLast
                    ? "p-4"
                    : "p-4 border-b border-blue-gray-50";
   
                  return (
                    <tr key={Id_proceso}>
                      <td className={classes}>
                        <Typography
                          variant="small"
                          color="blue-gray"
                          className="font-normal"
                        >
                          {PID}
                        </Typography>
                      </td>
                      <td className={classes}>
                        <Typography
                          variant="small"
                          color="blue-gray"
                          className="font-normal"
                        >
                          {Name}
                        </Typography>
                      </td>
                      <td className={classes}>
                        <Typography
                          variant="small"
                          color="blue-gray"
                          className="font-normal"
                        >
                          {User}
                        </Typography>
                      </td>
                      <td className={classes}>
                        <div className="w-max">
                          <Chip
                            variant="ghost"
                            size="sm"
                            value={State==0 ? "Running" : State==1 || State==1026?"Suspended":State==4 || State==128 || State==16?"Zombie":State==8 || State==260?"Stopped":"Unknow"}
                            color={State==0 ? "indigo" : State==1 || State==1026?"deep-orange":State==4 || State==128 || State==16?"green":State==8 || State==260?"red":"blue-gray"}
                          />
                        </div>
                      </td>
                      <td className={classes}>
                        <Typography
                          variant="small"
                          color="blue-gray"
                          className="font-normal"
                        >
                          {Math.round(Memory/(1024*1024)/(100-ram)*100)}%
                        </Typography>
                      </td>
                      <td className={classes}>
                        {actions?.map((a)=>{
                          return <Tooltip content={a.tool} key={a.tool}>
                            <IconButton variant="text" onClick={()=>a.func(PID)}>
                              {a.icon}
                            </IconButton>
                          </Tooltip>
                        })}
                        {Child.length>0?
                          <>
                            <Tooltip content={"Ver procesos hijo"}>
                              <IconButton variant="text" onClick={handleOpen}>
                                <EyeIcon className="h-4 w-4"/>
                              </IconButton>
                            </Tooltip>
                            <Dialog size="md"
                              open={open}
                              handler={handleOpen}
                              className="bg-transparent shadow-none">
                                <table  className="mt-4 w-full min-w-max table-auto text-left bg-sound-100">
                                  <thead>
                                    <tr>
                                      <th className="cursor-pointer border-y border-blue-gray-100 bg-blue-gray-50/50 p-4 transition-colors hover:bg-blue-gray-50"
                                      >
                                        <Typography
                                          variant="small"
                                          color="blue-gray"
                                          className="font-normal"
                                        >
                                          PID
                                        </Typography>
                                      </th>
                                      <th className="cursor-pointer border-y border-blue-gray-100 bg-blue-gray-50/50 p-4 transition-colors hover:bg-blue-gray-50"
                                      ><Typography
                                          variant="small"
                                          color="blue-gray"
                                          className="font-normal"
                                        >
                                          Nombre
                                        </Typography>
                                      </th>
                                      <th className="cursor-pointer border-y border-blue-gray-100 bg-blue-gray-50/50 p-4 transition-colors hover:bg-blue-gray-50"
                                      ><Typography
                                          variant="small"
                                          color="blue-gray"
                                          className="font-normal"
                                        >
                                          Accion
                                        </Typography>
                                      </th>
                                    </tr>
                                  </thead>
                                  <tbody>
                                    {Child?.map(
                                      ({PID, Name}, index) => {
                                        return <tr key={index}>
                                          <td className={classes}>
                                            <Typography
                                              variant="small"
                                              color="blue-gray"
                                              className="font-normal"
                                            >
                                              {PID}
                                            </Typography>
                                          </td>
                                          <td className={classes}>
                                            <Typography
                                              variant="small"
                                              color="blue-gray"
                                              className="font-normal"
                                            >
                                              {Name}
                                            </Typography>
                                          </td>
                                          <td className={classes}>
                                          <Tooltip content={"Ver procesos hijo"}>
                                            <IconButton variant="text" onClick={()=>kill(PID)}>
                                              <TrashIcon className="h-4 w-4"/>
                                            </IconButton>
                                          </Tooltip>
                                          </td>
                                        </tr>
                                      }
                                    )}
                                  </tbody>
                                </table>
                            </Dialog>
                          </>
                          :<></>
                        }
                      </td>
                    </tr>
                    
                  );
                 
                })}
            </tbody>
          </table>
        </CardBody>
        <CardFooter className="flex items-center justify-center border-t border-blue-gray-50 p-4">
        </CardFooter>
      </Card>
    );
  }
